<?phpnamespace app\forms;use row\core\Options;use row\utils\Email;use app\models;use app\specs\Output;class SimpleRequestAccountForm extends \row\form\SimpleForm {	protected function elements() {		return array(			'username' => array(				'type' => 'text',				'required' => 3, // 0 = don't care, 1 = must exist, 2 = must not be empty(), 3 = must more than whitespace				'validation' => function( $form ) {					$db = $form->application->db;					$usernameExists = models\User::count(array('username' => $form->input['username']));					return !$usernameExists;				}			),			'username_disclaimer' => array(				'type' => 'markup',				'text' => Output::translate('Have you read our %0?', array(Output::link(Output::translate('username guidelines', null, array('ucfirst' => false)), 'blog-user/guidelines/username')))			),			'password' => array(				'type' => 'password',				'required' => 1			),			'color' => array(				'title' => 'Favourite colour',				'type' => 'color',				'required' => 0			),			'bio' => array(				'type' => 'textarea',				'required' => 0,				'rows' => 5			),		);	}}