<?phpnamespace app\forms;use app\models;use app\specs\Output;class WithGrid extends \app\specs\SimpleForm {	protected function renderGridHorizontalTH( $element, $label ) {		$title = $label->description ? ' title="'.$label->description.'"' : '';		$html = '<th class="horizontal"'.$title.'>'.$label.'</th>';		return $html;	}	protected function elements( $defaults = null ) {		return array(			'password' => array(				'type' => 'password',				'description' => Output::translate('Admin password to prove you\'re one of the hims/hers.'),				'required' => true,				'minlength' => 4,			),			'prefs' => array(				'type' => 'grid',				'subtype' => 'checkbox', // or 'options' and then uncomment the next line//				'options' => models\Category::all(),				'reverse' => true, // reverses X and Y in **element names** (vertical is still vertical and horizontal is still horizontal)				'title' => Output::translate('Choose categories'),				'horizontal' => array(					Output::translate('Domain'),					models\Domain::all('domain_id >= ?', $this->options->domain->domain_id),				),				'vertical' => array(					Output::translate('User'),					models\User::all('user_id >= ?', $this->options->user->user_id),				),				'description' => Output::translate('One category per user / domain combination'),				'name' => 'options',			),			'cat' => array(				'type' => 'options',				'options' => models\Category::all(),			),		);	}}